import { useContext } from "react"
import { Context } from "../context"

export function List(){
    const {state,dispatch,ref2} = useContext(Context)
   console.log(ref2)
    return(
        <div>
        {state[0].map(function(item,index){
            if(state[1].includes(index)){
                return( <div key={Math.random()}>
                    <input key={Math.random()} ref={ref2}></input> 
                     <span key={ Math.random()} onClick={function(){dispatch({type:"clear", payload:index})}}>X</span>
                     <button onClick={function(){dispatch({type:"edit",payload:index,data:ref2.current.value})}}>Edit</button>
                </div>
                )
            }
            return<div key={Math.random()} onClick={function(){dispatch({type:"click",payload:index})}}>{item}

            <button key={Math.random()} onClick={function(e){
                e.stopPropagation()
                dispatch({type:"delete",payload:index})}

                                                                }> X</button>
            </div>
        })}
    </div>
    )
}
