import { useContext } from "react"
import { Context } from "../context"

export function List(){
    const {state,dispatch} = useContext(Context)
    return(
        <div>
        {state[0].map(function(item,index){
            if(state[1].includes(index)){
                return( <div key={Math.random()}>
                    <input key={Math.random()}></input> 
                     <span key={ Math.random()} onClick={function(){dispatch({type:"clear"})}}>X</span>
                </div>
                )
            }
            return<div key={Math.random()} onClick={function(){dispatch({type:"click",payload:index})}}>{item}</div>
        })}
    </div>
    )
}
